import React, { useState, useEffect } from 'react';
import axios from '../axiosConfig';
import '../styles/Friends.css';
import { useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowLeft, faUserXmark } from '@fortawesome/free-solid-svg-icons';

const Friends = () => {
    const [friendRequests, setFriendRequests] = useState([]);
    const [friends, setFriends] = useState([]);
    const [users, setUsers] = useState([]);
    const [selectedUser, setSelectedUser] = useState('');

    useEffect(() => {
        fetchFriendRequests();
        fetchFriends();
        fetchUsers();
    }, []);

    const fetchFriendRequests = async () => {
        try {
            const response = await axios.get('/friends/requests');
            setFriendRequests(response.data);
        } catch (error) {
            console.error('Error fetching friend requests:', error);
        }
    };

    const fetchFriends = async () => {
        try {
            const response = await axios.get('/friends');
            setFriends(response.data);
        } catch (error) {
            console.error('Error fetching friends:', error);
        }
    };

    const fetchUsers = async () => {
        try {
            const response = await axios.get('/users'); // Asumiendo que tienes un endpoint para obtener todos los usuarios
            setUsers(response.data);
        } catch (error) {
            console.error('Error fetching users:', error);
        }
    };

    const handleAcceptRequest = async (id) => {
        try {
            await axios.post(`/friends/requests/${id}/accept`);
            fetchFriendRequests();
            fetchFriends();
        } catch (error) {
            console.error('Error accepting friend request:', error);
        }
    };

    const handleRejectRequest = async (id) => {
        try {
            await axios.post(`/friends/requests/${id}/reject`);
            fetchFriendRequests();
        } catch (error) {
            console.error('Error rejecting friend request:', error);
        }
    };

    const handleSendRequest = async () => {
        try {
            await axios.post('/friends/requests', { userId: selectedUser });
            setSelectedUser('');
        } catch (error) {
            console.error('Error sending friend request:', error);
        }
    };

    return (
        <div>
        <div className="note-management-links">
            <button onClick={() => navigate('/notes')} className="user-management-button">
                <FontAwesomeIcon icon={faArrowLeft} /> Back to Notes
            </button>
            <Link to="/friends" className="note-management-button">
                <FontAwesomeIcon icon={faUserXmark} /> Add Friends
            </Link>
        </div>
        <div className="friends-container">
            
            <h1>Friend Requests</h1>
            <ul className="friend-requests-list">
                {friendRequests.map(request => (
                    <li key={request._id} className="friend-request-item">
                        <span>{request.name}</span>
                        <button onClick={() => handleAcceptRequest(request._id)}>Accept</button>
                        <button onClick={() => handleRejectRequest(request._id)}>Reject</button>
                    </li>
                ))}
            </ul>

            <h1>Friends</h1>
            <ul className="friends-list">
                {friends.map(friend => (
                    <li key={friend._id} className="friend-item">
                        {friend.name}
                    </li>
                ))}
            </ul>
        </div>
        </div>
    );
};

export default Friends;
