import React, { useState, useEffect } from 'react';
import axios from '../axiosConfig';
import { useNavigate } from 'react-router-dom';
import { Link } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUserCog, faClipboardList } from '@fortawesome/free-solid-svg-icons';

import '../styles/App.css';
import '../styles/Collection.css';  // Asegúrate de tener un archivo CSS para los estilos específicos de Collection

const Collection = () => {
  const [collection, setCollection] = useState([]);
  const [newCollectionName, setNewCollectionName] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    fetchCollection();
  }, []);

  const fetchCollection = async () => {
    try {
      const response = await axios.get('/collection');
      setCollection(response.data);
    } catch (error) {
      if (error.response && error.response.status === 401) {
        navigate('/login');  // Redirige al login si no está autenticado
      }
      console.error('Error fetching collection:', error);
    }
  };

  const handleCreateCollection = async () => {
    try {
      const response = await axios.post('/collection', { name: newCollectionName });
      setCollection([...collection, response.data]);
      setNewCollectionName('');
    } catch (error) {
      console.error('Error creating collection:', error);
    }
  };

  const handleDeleteCollection = async (id) => {
    try {
      await axios.delete(`/collection/${id}`);
      setCollection(collection.filter(collection => collection._id !== id));
    } catch (error) {
      console.error('Error deleting collection:', error);
    }
  };

  return (
    <div>
      <div className="collection-management-link">
        <Link to="/manage-users" className="collection-management-button">
          <FontAwesomeIcon icon={faUserCog} /> User Management
        </Link>
        <Link to="/notes" className="collection-management-button">
          <FontAwesomeIcon icon={faClipboardList} /> View Notes
        </Link>
      </div>
      <h1>Create Collection</h1>
      <div className="form-container">
        <input
          className="form-input"
          value={newCollectionName}
          onChange={(e) => setNewCollectionName(e.target.value)}
          placeholder="Enter new collection name"
        />
        <button onClick={handleCreateCollection} className="form-button">Add Collection</button>
      </div>
      <div className="collections-list">
        <h1>Collections</h1>
        <ul>
          {collection.map(collection => (
            <li key={collection._id} className="collection-item">
              <span className="collection-name">{collection.name}</span>
              <button onClick={() => handleDeleteCollection(collection._id)} className="delete-button">Delete</button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Collection;
