import React, { useState, useEffect } from 'react';
import axios from '../axiosConfig';
import '../styles/Note.css';

function Collection({ collection, deleteCollection }) {
    const [isEditing, setIsEditing] = useState(false);
    const [editedCollection, setEditedCollection] = useState({ ...collection });

    useEffect(() => {
        setEditedCollection({ ...collection });
    }, [collection]);

    const handleEditClick = () => {
        setIsEditing(true);
    };

    const handleCancelEdit = () => {
        setIsEditing(false);
        setEditedCollection({ ...collection });
    };

    const handleSaveEdit = () => {
        // Envía la solicitud de actualización al backend
        axios.put(`/collections/${collection._id}`, editedCollection)
            .then(response => {
                console.log("Respuesta después de actualizar:", response.data);
                setEditedCollection(response.data); // Actualiza la colección editada con los datos actualizados del servidor
                setIsEditing(false);
            })
            .catch(error => {
                console.error('Error updating collection:', error);
            });
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setEditedCollection(prevState => ({
            ...prevState,
            [name]: value
        }));
    };

    const handleDelete = () => {
        // Envía la solicitud de eliminación al backend
        axios.delete(`/collections/${collection._id}`)
            .then(() => {
                deleteCollection(collection._id); // Elimina la colección de la interfaz una vez que se elimina del backend
            })
            .catch(error => {
                console.error('Error deleting collection:', error);
            });
    };

    return (
        <div className="note-container">
            {isEditing ? (
                <div>
                    <input
                        type="text"
                        name="name"
                        value={editedCollection.name}
                        onChange={handleChange}
                        className="note-title"
                    />
                    <button onClick={handleSaveEdit} className="note-save-button">
                        Save
                    </button>
                    <button onClick={handleCancelEdit} className="note-delete-button">
                        Cancel
                    </button>
                </div>
            ) : (
                <div>
                    <h2 className="note-title">{collection.name}</h2>
                    <button onClick={handleEditClick} className="note-button">
                        Edit
                    </button>
                    <button onClick={handleDelete} className="note-delete-button">
                        Delete
                    </button>
                </div>
            )}
        </div>
    );
}

export default Collection;
