import React, { useState, useEffect } from 'react';
import axios from '../axiosConfig';
import { useNavigate } from 'react-router-dom';
import { Link } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUserCog } from '@fortawesome/free-solid-svg-icons';
import '../styles/Collection.css';

const Collection = () => {
    const [collections, setCollections] = useState([]);
    const [newCollectionName, setNewCollectionName] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        fetchCollections();
    }, []);

    const fetchCollections = async () => {
        try {
            const response = await axios.get('/collection/user');
            setCollections(response.data);
        } catch (error) {
            if (error.response && error.response.status === 401) {
                navigate('/login');  // Redirige al login si no est치 autenticado
            }
            console.error('Error fetching collections:', error);
        }
    };

    const handleCreateCollection = async () => {
        try {
          const response = await axios.post('/api/collection', { name: newCollectionName });
          console.log('Collection added successfully:', response.data);
          // Asegurarse de que la respuesta del servidor contiene los datos correctos
          if (response.data && response.status === 201) {
            // Agregar la nueva colecci칩n al estado
            setCollections(prevCollection => [...prevCollection, response.data]);
            // Limpiar el campo de entrada
            setNewCollectionName('');
          } else {
            throw new Error('Invalid server response');
          }
        } catch (error) {
          console.error('Error adding collection:', error);
        }
      };

    const handleDeleteCollection = async (id) => {
        try {
            await axios.delete(`/collection/${id}`);
            setCollections(collections.filter(collection => collection._id !== id));
        } catch (error) {
            console.error('Error deleting collection:', error);
        }
    };

    return (
        <div>
            <div className="collection-management-link">
                <Link to="/manage-users" className="collection-management-button">
                    <FontAwesomeIcon icon={faUserCog} /> User Management
                </Link>
                <Link to="/notes" className="collection-management-button">
                    <FontAwesomeIcon icon={faUserCog} /> Notes
                </Link>
            </div>
            <h1>Create Collection</h1>
            <div className="collection-form">
                <input
                    value={newCollectionName}
                    onChange={(e) => setNewCollectionName(e.target.value)}
                    placeholder="Enter new collection name"
                    className="collection-input"
                />
                <button onClick={handleCreateCollection} className="collection-button">Add Collection</button>
            </div>
            <div className="collections-container">
                {collections.map(collection => (
                    <div key={collection._id} className="collection-item">
                        <span>{collection.name}</span>
                        <button onClick={() => handleDeleteCollection(collection._id)} className="collection-delete-button">Delete</button>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Collection;
