import React, { useState, useEffect } from 'react';
import axios from '../axiosConfig';
import Collection from './Collection';
import { Link } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUserCog, faFolder } from '@fortawesome/free-solid-svg-icons';

import '../styles/App.css';
import '../styles/Note.css';

function CollectionList() {
    const [collections, setCollections] = useState([]);

    const fetchCollections = () => {
        axios.get('/collections')
            .then(response => setCollections(response.data))
            .catch(error => console.error('Error fetching collections:', error));
    };

    const handleCollectionAdded = (newCollection) => {
        setCollections(prevCollections => [...prevCollections, newCollection]);
    };

    const addCollection = (collection) => {
        axios.post('/collections', collection)
            .then(response => {
                console.log('Collection added successfully:', response.data);
                if (response.data && response.status === 201) {
                    setCollections(prevCollections => [...prevCollections, response.data]);
                } else {
                    throw new Error('Invalid server response');
                }
            })
            .catch(error => {
                console.error('Error adding collection:', error);
            });
    };

    const updateCollection = (id, updatedCollection) => {
        axios.put(`/collections/${id}`, updatedCollection)
            .then(response => {
                console.log("Respuesta después de actualizar:", response.data);
                setCollections(prevCollections => prevCollections.map(collection => collection._id === id ? { ...collection, ...response.data } : collection));
            })
            .catch(error => {
                console.error('Error updating collection:', error);
            });
    };

    const deleteCollection = (id) => {
        axios.delete(`/collections/${id}`)
            .then(() => {
                fetchCollections();
            })
            .catch(error => console.error('Error deleting collection:', error));
    };

    useEffect(() => {
        fetchCollections();
    }, []);

    return (
        <div>
            <div className="note-management-links">
                <Link to="/manage-users" className="note-management-button">
                    <FontAwesomeIcon icon={faUserCog} /> User Management
                </Link>
                <Link to="/collection" className="note-management-button">
                    <FontAwesomeIcon icon={faFolder} /> Collection
                </Link>
            </div>
            <h1>Create Collection</h1>
            {/* Aquí va el formulario para crear una nueva colección */}
            <div className="notes-flex-container">
                {collections.map(collection => (
                    <Collection
                        key={collection._id}
                        collection={collection}
                        deleteCollection={() => deleteCollection(collection._id)}
                        updateCollection={updateCollection}
                    />
                ))}
            </div>
        </div>
    );
}

export default CollectionList;
