import React, { useState, useEffect } from 'react';
import axios from '../axiosConfig';
import '../styles/Note.css';

function Collection({ collection, deleteCollection, updateCollection }) {
    const [isEditing, setIsEditing] = useState(false);
    const [editedCollection, setEditedCollection] = useState({ ...collection });

    useEffect(() => {
        setEditedCollection({ ...collection });
    }, [collection]);

    const handleEditClick = () => {
        setIsEditing(true);
    };

    const handleCancelEdit = () => {
        setIsEditing(false);
        setEditedCollection({ ...collection });
    };

    const handleSaveEdit = () => {
        updateCollection(collection._id, editedCollection);
        setIsEditing(false);
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setEditedCollection(prevState => ({
            ...prevState,
            [name]: value
        }));
    };

    return (
        <div className="note-container">
            {isEditing ? (
                <div>
                    <input
                        type="text"
                        name="name"
                        value={editedCollection.name}
                        onChange={handleChange}
                        className="note-title"
                    />
                    <button onClick={handleSaveEdit} className="note-save-button">
                        Save
                    </button>
                    <button onClick={handleCancelEdit} className="note-delete-button">
                        Cancel
                    </button>
                </div>
            ) : (
                <div>
                    <h2 className="collection-title">{collection.name}</h2>
                    <button onClick={handleEditClick} className="note-button">
                        Edit
                    </button>
                    <button onClick={() => deleteCollection(collection._id)} className="note-delete-button">
                        Delete
                    </button>
                </div>
            )}
        </div>
    );
}

export default Collection;
